mapper.insert:
INSERT INTO notify_sitemsg_record(ID,USER_ID,TITLE,CONTENT,BACKLOG_FLAG,SEND_BY
,SEND_TIME,CREATION_DATE,LAST_UPDATE_DATE,SENDER_TYPE)
select NOTIFY_SITEMSG_RECORD_S.NEXTVAL,T.* from(

    SELECT
    #{r.userId},#{r.title},#{r.content},#{r.backlogFlag},#{r.sendBy}
    ,#{r.sendTime},#{r.creationDate},#{r.lastUpdateDate},#{r.senderType}
    from dual

) T

mapper.select:
SELECT
nsr.*
FROM notify_sitemsg_record nsr
WHERE 1 = 1

    AND nsr.id = #{query.id}


    AND nsr.user_id = #{query.userId}


    AND nsr.title LIKE concat(concat('%',#{query.title}),'%')


    AND nsr.content LIKE concat(concat('%',#{query.content}),'%')


    AND nsr.is_read = #{query.read}


    AND (
    nsr.title LIKE concat(concat('%',#{query.title}),'%') OR
    nsr.content LIKE concat(concat('%',#{query.content}),'%')
    )

SELECT
nsr.*
FROM notify_sitemsg_record nsr
WHERE nsr.user_id = #{userId}

    AND nsr.is_read = #{read}


    AND nsr.backlog_flag = #{backlogFlag}

order by nsr.id desc
